    <!-- =============================== FOR MOBILE VIEW START ================================== -->

    <div class="Showmobile">
        <section id="mobilestep1" class="mobileregistersec bg-white">
            <div class="container">
                <div class="loginbox">
                    <div id="otpbox" class="text-center">
                        <!-- send otp  -->
                        <div class="px-2 px-col-4 py-4" id="mobile_send_otp">
                            <h5 class="mb-3">Enter Mobile Number to Send Otp </h5>

                            <form class="mt-3 text-center">
                                <div class="form-group px-4">
                                    <input type="text" class="form-control" placeholder="Enter Mobile Number" name="mobile" id="m_mobile">
                                </div>
                            </form>
                            <div class="mb-3 mt-4">
                                <button class="btn1 dsbl1" id="btn_mobile_send_otp" disabled>Send Otp</button>
                            </div>
                        </div>

                        <!-- verify otp -->

                        <div class="px-2 px-lg-4 py-4" id="mobile_verify_otp" style="display:none">
                            <h5 class="mb-3">Enter OTP you received on mobile</h5>
                            <!-- <p>One OTP has been sent, to your Mobile Number </p> -->
                            <small>Verify your OTP</small>
                            <form class="mt-3 otpboxdesign text-center">
                                <input class="otp" id="motp_1" type="text" onkeyup="tabChang(1)" maxlength="1" required>
                                <input class="otp" id="motp_2" type="text" onkeyup="tabChang(2)" maxlength="1" required>
                                <input class="otp" id="motp_3" type="text" onkeyup="tabChang(3)" maxlength="1" required>
                                <input class="otp" id="motp_4" type="text" onkeyup="tabChang(4)" maxlength="1" required>
                            </form>
                            <div class="mb-3 mt-4"><button id="btn_mobile_verify_otp" class="btn1 dsbl1" disabled>Verify Otp</button></div>
                            <small class="d-block"><a href="" id="btn_mobile_resend_otp">Resend OTP</a></small>
                        </div>
                    </div>
                </div>
            </div>
        </section>
    </div>

    <script>
        let tabChang = function(val) {
            let ele = document.querySelectorAll('input[type=text]');
            if (ele[val - 1].value != '') {
                ele[val].focus();
            } else if (ele[val - 1].value == '') {
                ele[val - 2].focus();
            }
        }
    </script>